package day09;

public class Breakwhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		while(a<=10) {
			if(a==5) 
				break;
				System.out.println("\n\t statement"+a);
				a++;
			
		}
		System.out.println("n\\t end of statement");

	}}//output is 
		// statement1

	 //statement2

	 //statement3

	// statement4
//n\t end of statement
